<template>
    <div>
        <button @click="addNode">新建根文件夹</button>
        <project-tree
          @click="onClick"
          @change-name="onChangeName"
          @delete-node="onDel"
          @add-node="onAddNode"
          :model="data"
          default-leaf-node-name="未命名"
          v-bind:default-expanded="false">
        </project-tree>
        <!-- <button @click="getNewTree">Get new tree</button>
        <pre>
          {{newTree}}
        </pre> -->
    </div>
</template>
<script>
  import { ProjectTree, Tree, TreeNode } from '../src'
  export default {
    components: {
      ProjectTree
    },
    data () {
      return {
        newTree: {},
        data: new Tree([
          {
            name: '项目1',
            id: 1,
            pid: 0,
            addTreeNodeDisabled: false,
            addLeafNodeDisabled: false,
            editNodeDisabled: false,
            delNodeDisabled: false,
            children: [
              {
                name: '项目1-1',
                id: 2,
                isLeaf: true,
                pid: 1
              }
            ]
          },
          {
            name: '项目2',
            id: 3,
            pid: 0,
            disabled: true
          },
          {
            name: '项目3',
            id: 4,
            pid: 0
          }
        ])
      }
    },
    methods: {
      onDel (node) {
        console.log(node)
        node.remove()
      },

      onChangeName (params) {
        console.log(params)
      },

      onAddNode (params) {
        console.log(params)
        console.log(this.data)
      },

      onClick (params) {
        console.log(params)
      },

      addNode () {
        var node = new TreeNode({ name: 'new node', isLeaf: false })
        if (!this.data.children) this.data.children = []
        this.data.addChildren(node)
      },

      getNewTree () {
        var vm = this
        function _dfs (oldNode) {
          var newNode = {}

          for (var k in oldNode) {
            if (k !== 'children' && k !== 'parent') {
              newNode[k] = oldNode[k]
            }
          }

          if (oldNode.children && oldNode.children.length > 0) {
            newNode.children = []
            for (var i = 0, len = oldNode.children.length; i < len; i++) {
              newNode.children.push(_dfs(oldNode.children[i]))
            }
          }
          return newNode
        }

        vm.newTree = _dfs(vm.data)
      },

      onClick(model) {
        console.log(model)
      }
    }
  }
</script>
